static void SSD1306_OLED_WriteData(i2c_bus_t bus, uint16_t dev_addr, i2c_data_t *data, uint8_t data_byte);
static void SSD1306_OLED_ShowChar(i2c_bus_t bus, uint16_t dev_addr, i2c_data_t *data, uint8_t line, uint8_t column, char ch);

// ----- User Functions -----

/**
 * @brief 初始化SSD 1306 OLED
 *
 * @param bus I2C总线id，参考 @ref i2c_bus_t 。
 * @param dev_addr 设备地址
 * @param data I2C数据结构体，参考 @ref i2c_data_t 。
 */
void SSD1306_OLED_Init(i2c_bus_t bus, uint16_t dev_addr, i2c_data_t *data)
{
    for (int i = 0; i < (int)(sizeof(SSD1306_OLED_init_Command) / sizeof(SSD1306_OLED_init_Command[0])); i++)
    {
        data->send_buf[0] = SSD1306_OLED_init_Command[i][0];
        data->send_buf[1] = SSD1306_OLED_init_Command[i][1];
        data->send_len = 2;
        if (uapi_i2c_master_write(bus, dev_addr, data) == ERRCODE_SUCC)
        {
            // osal_printk("i2c%d master send succ!, command: 0x%02X\r\n", bus, SSD1306_OLED_init_Command[i][1]);
            ;
        }
        else
        {
            // osal_printk("i2c%d master send failed!, command: 0x%02X\r\n", bus, SSD1306_OLED_init_Command[i][1]);
            ;
            continue;
        }
    }
    SSD1306_OLED_Clear(bus, dev_addr, data); // 清屏
}

/**
 * @brief OLED 显示字符串
 *
 * @param bus I2C总线id，参考 @ref i2c_bus_t 。
 * @param dev_addr 设备地址
 * @param data I2C数据结构体，参考 @ref i2c_data_t 。
 * @param line 显示行数，范围1~4
 * @param column 显示列数，范围1~16
 * @param str 显示的字符串
 */
void SSD1306_OLED_ShowString(i2c_bus_t bus, uint16_t dev_addr, i2c_data_t *data, uint8_t line, uint8_t column, char *str)
{
    for (int i = 0; str[i] != '\0'; i++)
    {
        SSD1306_OLED_ShowChar(bus, dev_addr, data, line, column + i, str[i]); // 显示字符
    }
}

/**
 * @brief OLED 显示整形数字，如果数字长度大于指定长度，则截断；如果数字长度小于指定长度，则在最后用空格填充
 *
 * @param bus I2C总线id，参考 @ref i2c_bus_t 。
 * @param dev_addr 设备地址
 * @param data I2C数据结构体，参考 @ref i2c_data_t 。
 * @param line 显示行数，范围1~4
 * @param column 显示列数，范围1~16
 * @param num 显示的数字
 * @param len 显示的数字长度
 * @param is_signed 是否为有符号数，如果为true，则num为有符号数，否则为无符号数
 * @attention 当为无符号数时，负数会被转换为0；当为有符号数时，在显示时会加上正负号
 */
void SSD1306_OLED_ShowIntNum(i2c_bus_t bus, uint16_t dev_addr, i2c_data_t *data, uint8_t line, uint8_t column, int num, int len, bool is_signed)
{
    char str[16] = {0}; // 字符串缓冲区，长度足够大以容纳所有数字和结束符
    if (is_signed)
    {
        if (num < 0)
        {
            snprintf(str, sizeof(str), "-%u", -num); // 将负数转换为字符串
        }
        else
        {
            snprintf(str, sizeof(str), "+%u", num);
        }
    }
    else
    {
        if (num < 0)
        {
            num = 0; // 如果是无符号数，负数直接置为0
        }
        snprintf(str, sizeof(str), "%u", num); // 将无符号数字转换为字符串
    }
    int str_len = strlen(str); // 获取字符串长度
    if (str_len > len)
    {
        str[len] = '\0'; // 截断字符串
    }
    else
    {
        for (int i = str_len; i < len; i++)
        {
            str[i] = ' '; // 用空格填充
        }
        str[len] = '\0'; // 添加结束符
    }
    SSD1306_OLED_ShowString(bus, dev_addr, data, line, column, str); // 显示字符串
}

/**
 * @brief OLED 清屏
 *
 * @param bus I2C总线id，参考 @ref i2c_bus_t 。
 * @param dev_addr I2C设备地址
 * @param data I2C数据结构体，参考 @ref i2c_data_t 。
 */
void SSD1306_OLED_Clear(i2c_bus_t bus, uint16_t dev_addr, i2c_data_t *data)
{
    for (int i = 0; i < 8; i++)
    {
        SSD1306_OLED_SetCursor(bus, dev_addr, data, i, 0); // 设置光标位置
        for (int j = 0; j < 128; j++)
        {
            SSD1306_OLED_WriteData(bus, dev_addr, data, 0x00); // 清屏
        }
    }
}

// ------ Internal Functions ------

/**
 * @brief OLED 显示字符
 *
 * @param bus I2C总线id，参考 @ref i2c_bus_t 。
 * @param dev_addr 设备地址
 * @param data I2C数据结构体，参考 @ref i2c_data_t 。
 * @param line 显示行数，范围1~4
 * @param column 显示列数，范围1~16
 * @param ch 显示的字符
 * @attention line和column的范围要在 OLED 显示范围内
 * @attention 字符集范围为ASCII码表中的可显示字符
 */
static void SSD1306_OLED_ShowChar(i2c_bus_t bus, uint16_t dev_addr, i2c_data_t *data, uint8_t line, uint8_t column, char ch)
{
    SSD1306_OLED_SetCursor(bus, dev_addr, data, (line - 1) * 2, (column - 1) * 8); // 设置光标位置
    for (int i = 0; i < 8; i++)
    {
        SSD1306_OLED_WriteData(bus, dev_addr, data, SSD1306_OLED_F8x16[ch - ' '][i]); // 发送数据
    }
    SSD1306_OLED_SetCursor(bus, dev_addr, data, (line - 1) * 2 + 1, (column - 1) * 8); // 设置光标位置
    for (int i = 0; i < 8; i++)
    {
        SSD1306_OLED_WriteData(bus, dev_addr, data, SSD1306_OLED_F8x16[ch - ' '][i + 8]); // 发送数据
    }
}

/**
 * @brief 发送命令到 OLED
 *
 * @param bus I2C总线id，参考 @ref i2c_bus_t 。
 * @param dev_addr 设备地址
 * @param data I2C数据结构体，参考 @ref i2c_data_t 。
 * @param command 命令字节
 */
static void SSD1306_OLED_SendCommand(i2c_bus_t bus, uint16_t dev_addr, i2c_data_t *data, uint8_t command)
{
    data->send_buf[0] = 0x00;    // 控制字节，0x00表示写命令
    data->send_buf[1] = command; // 命令字节
    data->send_len = 2;
    if (uapi_i2c_master_write(bus, dev_addr, data) == ERRCODE_SUCC)
    {
        // osal_printk("i2c%d master send command succ!, command: 0x%02X\r\n", bus, command);
        ;
    }
    else
    {
        // osal_printk("i2c%d master send command failed!, command: 0x%02X\r\n", bus, command);
        ;
        return;
    }
}

/**
 * @brief 设置 OLED 显示位置
 *
 * @param bus I2C总线id，参考 @ref i2c_bus_t 。
 * @param dev_addr 设备地址
 * @param data I2C数据结构体，参考 @ref i2c_data_t 。
 * @param x x坐标
 * @param y y坐标
 * @attention x坐标范围：0~127，y坐标范围：0~7
 */
static void SSD1306_OLED_SetCursor(i2c_bus_t bus, uint16_t dev_addr, i2c_data_t *data, uint8_t y, uint8_t x)
{
    SSD1306_OLED_SendCommand(bus, dev_addr, data, 0xB0 | y);                 // 设置页地址
    SSD1306_OLED_SendCommand(bus, dev_addr, data, ((x & 0xF0) >> 4) | 0x10); // 设置高四位列地址
    SSD1306_OLED_SendCommand(bus, dev_addr, data, 0x00 | (x & 0x0F));        // 设置低四位列地址
}

/**
 * @brief 发送数据到 OLED
 *
 * @param bus I2C总线id，参考 @ref i2c_bus_t 。
 * @param dev_addr 设备地址
 * @param data I2C数据结构体，参考 @ref i2c_data_t 。
 * @param data_byte 发送的数据字节
 */
static void SSD1306_OLED_WriteData(i2c_bus_t bus, uint16_t dev_addr, i2c_data_t *data, uint8_t data_byte)
{
    data->send_buf[0] = 0x40;      // 控制字节，0x40表示写数据
    data->send_buf[1] = data_byte; // 数据字节
    data->send_len = 2;
    if (uapi_i2c_master_write(bus, dev_addr, data) == ERRCODE_SUCC)
    {
        // osal_printk("i2c%d master send data succ!, data: 0x%02X\r\n", bus, data_byte);
        ;
    }
    else
    {
        // osal_printk("i2c%d master send data failed!, data: 0x%02X\r\n", bus, data_byte);
        ;
        return;
    }
}

